import React from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ReactFC, cn } from "@/lib/utils";
import { Drawer } from "vaul";
import { DrawerContext } from "@/registry/new-york/bottom-drawer/DrawerContext";
import { useControlledOpen } from "@/registry/hooks/useControlledOpen";

export interface BottomDrawerClassNames {
  overlay?: string;
  content?: string;
  handle?: string;
  title?: string;
  childrenWrapper?: string;
}

export interface BottomDrawerProps {
  title?: string;
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
  trigger?: React.ReactNode;
  classNames?: BottomDrawerClassNames;
  renderHeader?:
    | ((props: {
        handle: React.ReactNode;
        close: () => void;
      }) => React.ReactNode)
    | null;
}

export const BottomDrawer: ReactFC<BottomDrawerProps> = ({
  children,
  title,
  open,
  onOpenChange,
  trigger,
  classNames,
  renderHeader,
}) => {
  const { isOpen, setIsOpen, close } = useControlledOpen({
    open,
    onOpenChange,
  });

  const handle = (
    <div
      className={cn(
        "mx-auto w-12 h-1.5 flex-shrink-0 rounded-full bg-zinc-300 dark:bg-zinc-700 mb-8",
        classNames?.handle
      )}
    />
  );

  const defaultHeader = (
    <>
      {handle}
      {title && (
        <div
          className={cn(
            "text-lg font-semibold mb-4 text-zinc-900 dark:text-white",
            classNames?.title
          )}
        >
          {title}
        </div>
      )}
    </>
  );

  const noHeader = renderHeader === null;

  return (
    <DrawerContext.Provider value={{ close }}>
      <Drawer.Root open={isOpen} onOpenChange={setIsOpen}>
        <AnimatePresence>
          {isOpen && (
            <motion.div
              initial={{ scale: 1 }}
              animate={{ scale: 0.95 }}
              exit={{ scale: 1 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="fixed inset-0 z-30 pointer-events-none"
            >
              <div className="w-full h-full origin-top">
                <div className="h-full overflow-hidden">
                  <div className="opacity-0">{trigger}</div>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        <Drawer.Trigger asChild>{trigger}</Drawer.Trigger>

        <Drawer.Portal>
          <Drawer.Overlay
            className={cn(
              "fixed inset-0 bg-black/40 backdrop-blur-md dark:bg-black/60 z-[9999]",
              classNames?.overlay
            )}
          />
          <Drawer.Content
            className={cn(
              "bg-white dark:bg-zinc-900 w-[95%] flex flex-col rounded-t-[10px] fixed bottom-0 left-0 right-0 z-[9999] mx-auto",
              "dark:border-t dark:border-zinc-800",
              "max-w-[500px]",
              classNames?.content,
              {
                "py-6": !noHeader,
              }
            )}
          >
            <div
              className={cn(
                "px-6 pb-6 md:pb-2 bg-white dark:bg-zinc-900 rounded-t-[10px] flex-1 overflow-y-auto",
                classNames?.childrenWrapper
              )}
            >
              {noHeader
                ? null
                : renderHeader
                ? renderHeader({ handle, close })
                : defaultHeader}
              {children}
            </div>
          </Drawer.Content>
        </Drawer.Portal>
      </Drawer.Root>
    </DrawerContext.Provider>
  );
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { BottomDrawer } from "./BottomDrawer";
import { MenuProvider } from "@/registry/new-york/menu-context/MenuContext";

export interface BottomDrawerMenuProps {
  children: React.ReactNode;
  content: React.ReactNode;
  title?: string;
}

export const BottomDrawerMenu: ReactFC<BottomDrawerMenuProps> = ({
  children,
  content,
  title,
}) => {
  return (
    <BottomDrawer
      title={title}
      trigger={children}
      classNames={{
        childrenWrapper: "px-0",
      }}
    >
      <MenuProvider menuType="bottom-drawer">
        <div className="flex flex-col">{content}</div>
      </MenuProvider>
    </BottomDrawer>
  );
};

import { createContext, useContext } from "react";

interface DrawerContextType {
  close: () => void;
}

export const DrawerContext = createContext<DrawerContextType | null>(null);

export const useDrawer = () => {
  const context = useContext(DrawerContext);
  if (!context) {
    throw new Error("useDrawer must be used within a DrawerProvider");
  }
  return context;
};

import React from "react";
import { ReactFC, cn } from "@/lib/utils";
import { LucideIcon } from "lucide-react";
import { HelpInfoCircle } from "@/registry/new-york/help-info-circle/HelpInfoCircle";

export interface BottomDrawerMenuItemProps {
  children: React.ReactNode;
  leftIcon?: LucideIcon;
  rightIcon?: LucideIcon;
  className?: string;
  disabled?: boolean;
  destructive?: boolean;
  onClick?: () => void;
  href?: string;
  external?: boolean;
  isLast?: boolean;
  shortcut?: string;
  hint?: string;
}

export const BottomDrawerMenuItem: ReactFC<BottomDrawerMenuItemProps> = ({
  children,
  leftIcon: LeftIcon,
  rightIcon: RightIcon,
  className,
  disabled,
  destructive,
  onClick,
  href,
  external,
  isLast,
  shortcut,
  hint,
}) => {
  const Component = href ? (external ? "a" : "a") : "button";
  const linkProps = href
    ? {
        href,
        ...(external && {
          target: "_blank",
          rel: "noopener noreferrer",
        }),
      }
    : {};

  const iconClasses = cn(
    "h-5 w-5",
    "text-muted-foreground",
    destructive && "text-destructive"
  );

  const itemClasses = cn(
    "flex items-center w-full px-4 py-3 text-base",
    !isLast && "border-b border-zinc-100 dark:border-zinc-800",
    "transition-colors active:bg-zinc-50 dark:active:bg-zinc-800/50",
    "text-left justify-start",
    disabled && "opacity-50 pointer-events-none",
    destructive && "text-destructive",
    className
  );

  return (
    <Component
      className={itemClasses}
      onClick={onClick}
      disabled={disabled && Component === "button"}
      {...linkProps}
    >
      {LeftIcon && (
        <LeftIcon className={cn("mr-3 flex-shrink-0", iconClasses)} />
      )}
      <span className="flex-1">{children}</span>
      {hint && (
        <div className="ml-2 flex-shrink-0">
          <HelpInfoCircle
            content={hint}
            drawerTitle="Help"
            iconClassName={destructive ? "text-destructive" : ""}
            isMobile={true}
          />
        </div>
      )}
      {RightIcon && (
        <RightIcon className={cn("ml-auto flex-shrink-0", iconClasses)} />
      )}
    </Component>
  );
};

import React from "react";
import { ReactFC, cn } from "@/lib/utils";

// Label
export interface BottomDrawerMenuLabelProps {
  children: React.ReactNode;
  className?: string;
}

export const BottomDrawerMenuLabel: ReactFC<BottomDrawerMenuLabelProps> = ({
  children,
  className,
}) => {
  return (
    <div
      className={cn(
        "px-4 py-2 text-sm font-medium text-zinc-500 dark:text-zinc-400 text-left",
        className
      )}
    >
      {children}
    </div>
  );
};

// Separator
export interface BottomDrawerMenuSeparatorProps {
  className?: string;
}

export const BottomDrawerMenuSeparator: ReactFC<
  BottomDrawerMenuSeparatorProps
> = ({ className }) => {
  return (
    <div
      className={cn(
        "h-[1px] my-1 bg-zinc-100 dark:bg-zinc-800 w-full",
        className
      )}
    />
  );
};

// Group
export interface BottomDrawerMenuGroupProps {
  children: React.ReactNode;
  className?: string;
}

export const BottomDrawerMenuGroup: ReactFC<BottomDrawerMenuGroupProps> = ({
  children,
  className,
}) => {
  return (
    <div className={cn("flex flex-col w-full", className)}>{children}</div>
  );
};

import React from "react";
import { BottomDrawerMenu } from "./BottomDrawerMenu";
import { CommonMenuItem } from "@/registry/new-york/simple-dropdown-menu/CommonMenuItem";
import { CommonMenuLabel } from "@/registry/new-york/simple-dropdown-menu/CommonMenuLabel";
import { CommonMenuSeparator } from "@/registry/new-york/simple-dropdown-menu/CommonMenuSeparator";
import { CommonMenuGroup } from "@/registry/new-york/simple-dropdown-menu/CommonMenuGroup";
import { Button } from "@/components/ui/button";
import {
  User,
  Settings,
  LogOut,
  Bell,
  Bookmark,
  Edit,
  Trash,
  HelpCircle,
} from "lucide-react";

export default function BottomDrawerMenuPreview() {
  return (
    <div className="flex flex-col gap-8">
      <div className="flex flex-wrap items-center justify-center gap-8 p-8 border rounded-lg">
        <BottomDrawerMenu
          content={
            <>
              <CommonMenuLabel>My Account</CommonMenuLabel>
              <CommonMenuSeparator />
              <CommonMenuGroup>
                <CommonMenuItem leftIcon={User}>Profile</CommonMenuItem>
                <CommonMenuItem leftIcon={Settings}>Settings</CommonMenuItem>
                <CommonMenuItem leftIcon={Bell}>Notifications</CommonMenuItem>
                <CommonMenuItem leftIcon={Bookmark}>Saved Items</CommonMenuItem>
              </CommonMenuGroup>
              <CommonMenuSeparator />
              <CommonMenuItem leftIcon={LogOut} destructive>
                Log out
              </CommonMenuItem>
            </>
          }
        >
          <Button variant="outline">User Menu</Button>
        </BottomDrawerMenu>

        <BottomDrawerMenu
          content={
            <>
              <CommonMenuLabel>Actions</CommonMenuLabel>
              <CommonMenuItem leftIcon={Edit}>Edit</CommonMenuItem>
              <CommonMenuItem leftIcon={Bookmark}>Bookmark</CommonMenuItem>
              <CommonMenuItem
                leftIcon={HelpCircle}
                hint="Get detailed information about this document"
              >
                Help
              </CommonMenuItem>
              <CommonMenuItem leftIcon={Trash} destructive>
                Delete
              </CommonMenuItem>
            </>
          }
        >
          <Button>Document Actions</Button>
        </BottomDrawerMenu>
      </div>
    </div>
  );
}
