"use client";

import * as React from "react";
import { ReactFC } from "@/lib/types";
import { Spinner, SpinnerProps } from "@/registry/new-york/spinner/Spinner";

export interface FullPageSpinnerProps extends SpinnerProps {}

export const FullPageSpinner: ReactFC<FullPageSpinnerProps> = ({
  size = "xl",
  ...props
}) => {
  return (
    <div className="min-h-screen min-w-screen flex items-center justify-center">
      <Spinner size={size} {...props} />
    </div>
  );
};

import { PropsWithChildren } from "react";
import { LucideIcon } from "lucide-react";

export type ReactFC<T> = React.FC<PropsWithChildren & T>;
export type Size = "xs" | "sm" | "md" | "lg" | "xl";
export interface SelectOption {
  value: string;
  label?: string;
  emoji?: string;
  icon?: LucideIcon;
  closeOnClick?: boolean;
  disabled?: boolean;
}
