import React from "react";
import { ReactFC } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { MenuProvider } from "@/registry/new-york/menu-context/MenuContext";
import { useControlledOpen } from "@/registry/hooks/useControlledOpen";
import { useKitzeUI } from "@/registry/new-york/kitze-ui-context/KitzeUIContext";
import { BottomDrawerMenu } from "@/registry/new-york/bottom-drawer/BottomDrawerMenu";

// Define MobileViewType for Dropdown
export type DropdownMobileViewType = "keep" | "bottom-drawer";

export interface SimpleDropdownMenuClassNames {
  content?: string;
  drawerContent?: string; // Optional class for drawer content wrapper
}

export interface SimpleDropdownMenuProps {
  children: React.ReactNode; // This is the trigger
  content: React.ReactNode; // This is the menu content
  classNames?: SimpleDropdownMenuClassNames;
  align?: "start" | "center" | "end";
  side?: "top" | "right" | "bottom" | "left";
  closeOnClick?: boolean;
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
  // Mobile specific props
  mobileView?: DropdownMobileViewType;
  drawerTitle?: string;
}

export const SimpleDropdownMenu: ReactFC<SimpleDropdownMenuProps> = ({
  children,
  content,
  classNames = {},
  align = "center",
  side = "bottom",
  closeOnClick = true,
  open,
  onOpenChange,
  mobileView = "keep",
  drawerTitle,
}) => {
  const { isMobile } = useKitzeUI();
  const { isOpen, setIsOpen, close } = useControlledOpen({
    open,
    onOpenChange,
  });

  // Function to close the menu when clicking on menu items
  const closeMenu = () => {
    if (closeOnClick) {
      close();
    }
  };

  // --- Mobile Rendering (Bottom Drawer) ---
  if (isMobile && mobileView === "bottom-drawer") {
    return (
      <BottomDrawerMenu
        title={drawerTitle}
        open={isOpen}
        onOpenChange={setIsOpen}
        content={content}
        closeOnClick={closeOnClick}
      >
        {children}
      </BottomDrawerMenu>
    );
  }

  // --- Default Dropdown Rendering (Desktop or mobileView='keep') ---
  return (
    <MenuProvider menuType="dropdown" closeMenu={closeMenu}>
      <DropdownMenu open={isOpen} onOpenChange={setIsOpen}>
        <DropdownMenuTrigger asChild>{children}</DropdownMenuTrigger>
        <DropdownMenuContent
          className={classNames.content} // Class for desktop dropdown
          align={align}
          side={side}
        >
          {content}
        </DropdownMenuContent>
      </DropdownMenu>
    </MenuProvider>
  );
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { DropdownMenuItem } from "@/components/ui/dropdown-menu";
import { ContextMenuItem } from "@/components/ui/context-menu";
import { DropdownMenuShortcut } from "@/components/ui/dropdown-menu";
import { ContextMenuShortcut } from "@/components/ui/context-menu";
import {
  useLinkableComponent,
  LinkableProps,
} from "@/registry/hooks/useLinkableComponent";
import { cn } from "@/lib/utils";
import { LucideIcon } from "lucide-react";
import { useMenuContext } from "@/registry/new-york/menu-context/MenuContext";
import { BottomDrawerMenuItem } from "@/registry/new-york/bottom-drawer/BottomDrawerMenuItem";
import { HelpInfoCircle } from "@/registry/new-york/help-info-circle/HelpInfoCircle";

export interface CommonMenuItemProps extends LinkableProps {
  children: React.ReactNode;
  shortcut?: string;
  hint?: string;
  leftIcon?: LucideIcon;
  rightIcon?: LucideIcon;
  className?: string;
  rootClassName?: string;
  textClassName?: string;
  leftIconClassName?: string;
  rightIconClassName?: string;
  shortcutClassName?: string;
  hintClassName?: string;
  disabled?: boolean;
  destructive?: boolean;
  onSelect?: () => void;
  isLast?: boolean;
  href?: string;
  onClick?: () => void;
  external?: boolean;
}

export const CommonMenuItem: ReactFC<CommonMenuItemProps> = ({
  children,
  shortcut,
  hint,
  leftIcon,
  rightIcon,
  className,
  rootClassName,
  textClassName,
  leftIconClassName,
  rightIconClassName,
  shortcutClassName,
  hintClassName,
  disabled,
  destructive,
  onSelect,
  isLast,
  onClick,
  external,
  ...rest
}) => {
  const { menuType, closeMenu } = useMenuContext();

  // Handle click with menu closing
  const handleClick = () => {
    onSelect?.();
    onClick?.();
    closeMenu?.();
  };

  // If we're in a bottom drawer, render the BottomDrawerMenuItem
  if (menuType === "bottom-drawer") {
    return (
      <BottomDrawerMenuItem
        leftIcon={leftIcon}
        rightIcon={rightIcon}
        className={cn(className, rootClassName)}
        disabled={disabled}
        destructive={destructive}
        onClick={handleClick}
        href={rest.href}
        external={external}
        isLast={isLast}
        hint={hint}
      >
        {children}
      </BottomDrawerMenuItem>
    );
  }

  // For dropdown and context menus, use the existing implementation
  const { Component, href, linkProps } = useLinkableComponent(rest);

  const MenuItem = menuType === "dropdown" ? DropdownMenuItem : ContextMenuItem;
  const MenuShortcut =
    menuType === "dropdown" ? DropdownMenuShortcut : ContextMenuShortcut;

  const iconClasses = cn(
    "h-4 w-4",
    "text-muted-foreground group-hover:text-current",
    destructive && "text-destructive group-hover:text-destructive"
  );

  const itemClasses = cn(
    className,
    rootClassName,
    destructive && "text-destructive"
  );

  const content = (
    <>
      {leftIcon &&
        React.createElement(leftIcon, {
          className: cn("mr-2", iconClasses, leftIconClassName),
        })}
      <span className={textClassName}>{children}</span>
      {hint && (
        <span className={cn("ml-2", hintClassName)}>
          <HelpInfoCircle
            content={hint}
            iconClassName={cn("h-3.5 w-3.5", destructive && "text-destructive")}
          />
        </span>
      )}
      {rightIcon &&
        React.createElement(rightIcon, {
          className: cn("ml-auto", iconClasses, rightIconClassName),
        })}
      {shortcut && (
        <MenuShortcut className={shortcutClassName}>{shortcut}</MenuShortcut>
      )}
    </>
  );

  // With link
  if (href && Component !== "div") {
    return (
      <MenuItem
        className={cn(itemClasses, "group")}
        disabled={disabled}
        onClick={handleClick}
        asChild
      >
        <Component href={href} {...linkProps}>
          {content}
        </Component>
      </MenuItem>
    );
  }

  // Without link
  return (
    <MenuItem
      className={cn(itemClasses, "group")}
      disabled={disabled}
      onClick={handleClick}
    >
      {content}
    </MenuItem>
  );
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { CommonMenuItem, CommonMenuItemProps } from "./CommonMenuItem";
import { Edit } from "lucide-react";

export interface CommonMenuItemEditProps
  extends Omit<CommonMenuItemProps, "children" | "leftIcon"> {
  label?: string;
}

export const CommonMenuItemEdit: ReactFC<CommonMenuItemEditProps> = ({
  label = "Edit",
  ...props
}) => {
  return (
    <CommonMenuItem leftIcon={Edit} {...props}>
      {label}
    </CommonMenuItem>
  );
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { CommonMenuItem, CommonMenuItemProps } from "./CommonMenuItem";
import { Trash } from "lucide-react";
import { useConfirmAlertDelete } from "@/registry/new-york/ui-alert/AlertContext";

export interface CommonMenuItemDeleteProps
  extends Omit<
    CommonMenuItemProps,
    "children" | "leftIcon" | "destructive" | "onSelect"
  > {
  label?: string;
  itemName?: string;
  onDelete?: () => void;
}

export const CommonMenuItemDelete: ReactFC<CommonMenuItemDeleteProps> = ({
  label = "Delete",
  itemName = "item",
  onDelete,
  ...props
}) => {
  const confirmDelete = useConfirmAlertDelete();

  const handleDelete = () => {
    confirmDelete({
      title: `Delete ${itemName}`,
      description: `Are you sure you want to delete this ${itemName}? This action cannot be undone.`,
      onConfirm: () => {
        onDelete?.();
      },
    });
  };

  return (
    <CommonMenuItem
      leftIcon={Trash}
      destructive
      onSelect={handleDelete}
      {...props}
    >
      {label}
    </CommonMenuItem>
  );
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { DropdownMenuLabel } from "@/components/ui/dropdown-menu";
import { ContextMenuLabel } from "@/components/ui/context-menu";
import { useMenuContext } from "@/registry/new-york/menu-context/MenuContext";
import { cn } from "@/lib/utils";
import { BottomDrawerMenuLabel } from "@/registry/new-york/bottom-drawer/BottomDrawerMenuComponents";

export interface CommonMenuLabelProps {
  children: React.ReactNode;
  className?: string;
}

export const CommonMenuLabel: ReactFC<CommonMenuLabelProps> = ({
  children,
  className,
}) => {
  const { menuType } = useMenuContext();

  // If bottom drawer, use BottomDrawerMenuLabel
  if (menuType === "bottom-drawer") {
    return (
      <BottomDrawerMenuLabel className={className}>
        {children}
      </BottomDrawerMenuLabel>
    );
  }

  // Otherwise use dropdown or context menu label
  const MenuLabel =
    menuType === "dropdown" ? DropdownMenuLabel : ContextMenuLabel;

  return <MenuLabel className={cn(className)}>{children}</MenuLabel>;
};

import React from "react";
import { ReactFC } from "@/lib/utils";
import { DropdownMenuSeparator } from "@/components/ui/dropdown-menu";
import { ContextMenuSeparator } from "@/components/ui/context-menu";
import { useMenuContext } from "@/registry/new-york/menu-context/MenuContext";
import { cn } from "@/lib/utils";
import { BottomDrawerMenuSeparator } from "@/registry/new-york/bottom-drawer/BottomDrawerMenuComponents";

export interface CommonMenuSeparatorProps {
  className?: string;
}

export const CommonMenuSeparator: ReactFC<CommonMenuSeparatorProps> = ({
  className,
}) => {
  const { menuType } = useMenuContext();

  // If bottom drawer, use BottomDrawerMenuSeparator
  if (menuType === "bottom-drawer") {
    return <BottomDrawerMenuSeparator className={className} />;
  }

  // Otherwise use dropdown or context menu separator
  const MenuSeparator =
    menuType === "dropdown" ? DropdownMenuSeparator : ContextMenuSeparator;

  return <MenuSeparator className={cn(className)} />;
};

