import * as React from "react";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";

export interface SimplePopoverProps {
  trigger: React.ReactNode;
  content: React.ReactNode;
  classNames?: {
    content?: string;
    trigger?: string;
  };
  align?: "start" | "center" | "end";
  sideOffset?: number;
}

export const SimplePopover = ({
  trigger,
  content,
  classNames = {},
  align = "center",
  sideOffset = 4,
}: SimplePopoverProps) => {
  return (
    <Popover>
      <PopoverTrigger className={classNames.trigger}>{trigger}</PopoverTrigger>
      <PopoverContent
        className={cn("w-auto min-w-[200px]", classNames.content)}
        align={align}
        sideOffset={sideOffset}
      >
        {content}
      </PopoverContent>
    </Popover>
  );
};

import React from "react";
import { SimplePopover } from "./index";
import { Button } from "@/components/ui/button";

export default function SimplePopoverPreview() {
  return (
    <div className="flex items-center justify-center p-8">
      <SimplePopover
        trigger={<Button variant="outline">Open Popover</Button>}
        content={
          <div className="p-2">
            <h4 className="font-medium mb-2">Popover Title</h4>
            <p className="text-sm text-muted-foreground">
              This is the popover content.
            </p>
          </div>
        }
      />
    </div>
  );
}
