"use client";

import { useTheme } from "next-themes";
import { ReactFC } from "@/lib/types";
import { useMounted } from "@/registry/hooks/useMounted";
import { ThemeSwitchSlider } from "@/registry/new-york/theme-switch-slider/ThemeSwitchSlider";

export interface ThemeSwitchNextThemesProps {
  className?: string;
}

export const ThemeSwitchNextThemes: ReactFC<ThemeSwitchNextThemesProps> = ({
  className,
}) => {
  const { theme, setTheme } = useTheme();
  const isDark = theme === "dark";
  const toggleTheme = () => setTheme(isDark ? "light" : "dark");
  const mounted = useMounted();

  if (!mounted) {
    return null;
  }

  return (
    <ThemeSwitchSlider
      theme={theme || "light"}
      setTheme={setTheme}
      isDark={isDark}
      toggleTheme={toggleTheme}
      className={className}
    />
  );
};

import { PropsWithChildren } from "react";

export type ReactFC<T> = React.FC<PropsWithChildren & T>;
export type Size = "xs" | "sm" | "md" | "lg" | "xl";
export interface SelectOption {
  value: string;
  label?: string;
  emoji?: string;
  icon?: any;
  closeOnClick?: boolean;
  disabled?: boolean;
}

"use client";

import { useEffect, useState } from "react";

export const useMounted = () => {
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
  }, []);

  return mounted;
};
